package com.qa.pages;

public class SwitchClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String game = "soccer" ;
		
		switch(game) {
		
		case "tennis":
			System.out.println("tesnnis");
		break;
		
		case "cocco":
			System.out.println("cocoo");
			break;
		default:
			System.out.println("none of  the games matched");
			
		}

	}

}
